var room = 1;

function catalogo_cie() {

    room++;
    var objTo = document.getElementById('catalogo_cie')
    var divtest = document.createElement("div");
    divtest.setAttribute("class", "form-group removeclass" + room);
    var rdiv = 'removeclass' + room;
    divtest.innerHTML = 
    '<div class="col-sm-4 "><div class="form-group"><div class="input-group">  <input type="text" class="form-control" id="catalogo_cie10" name="Schoolname[]" value="" placeholder="Catalogo CIE 10"> <div class="input-group-append"> <button class="btn btn-danger" type="button" onclick="remove_catalogo_cie(' + room + ');"> <i class="fa fa-minus"></i> </button></div></div></div></div><div class="clear">';

    objTo.appendChild(divtest)
}

function remove_catalogo_cie(rid) {
    $('.removeclass' + rid).remove();
}


